    <style>
        .sidebar {
            width: 350px;
            background-color: #2F3349;
            padding: 15px;
            position: initial;
            height: 100vh

        }

        .sidebar-menu {
            list-style-type: none;
            padding: 0;
            margin: 10px;
        }
        .dropdown-label .arrowsvg{
            margin-left: auto;

        }

        .sidebar-menu li {
            position: relative;

        }

        .sidebar-menu li a {
            text-decoration: none;
            color: #FFFFFF;

            font-family: 'Poppins', sans-serif;
            font-style: 'bold';

        }
        .logo{
            padding: 16px
        }

        .sidebar-menu li.active {
            font-weight: 200;
            color: #FFFFFF;
            padding-left: 30px;
            padding-top:8px;
            padding-bottom: 8px;
            border-radius: 6px;
            background: linear-gradient(90deg, #7367F0 41.45%, #423B8A 120%);


        }
        .dropdown-label{
            display: flex;
            padding: 12px;
            align-items: center;
        }
        .dropdown-label.active{
            background-color: #3D4056;
            border-radius: 6px;
        }
        .dropdown-label .title{
            margin-left: 12px;
            font-weight: 600;
        }

        .dropdown {
            list-style-type: none;
            margin: 0;
            display: none;
            margin-bottom: 20px;
        }

        .dropdown li {
            margin: 15px 0;
            padding-left: 30px;
        }

        .dropdown li a {
            color: #FFFFFF;
            font-weight: 200;
            margin-left: 10px;
        }

        .sidebar-menu li .dropdown {
            display: block;
        }
    </style>
